source_code.script:
$logDate = Get-Date -UFormat  "%Y%m%d"
$scriptPath = "E:\SailPoint\Scripts\AD_After_Create\After_Create_Script.ps1"
$enableDebug = $true

#====================-------Helper functions-------====================
function logToFile([String] $info){
    $(Get-Date -Format s) + ":  " + $info | Out-File $logFile -Append
}

#====================-------Get the request object-------====================
Try{
    Add-type -path C:\SailPoint\IQService\Utils.dll;
    $sReader = New-Object System.IO.StringReader([System.String]$env:Request);
    $xmlReader = [System.xml.XmlTextReader]([sailpoint.utils.xml.XmlUtil]::getReader($sReader));
    $requestObject = New-Object Sailpoint.Utils.objects.AccountRequest($xmlReader);
    $requestAsString = $env:Request

    #XML object
    $xmlRequest = [xml]$requestAsString
    $Username = $xmlRequest.AccountRequest.AttributeRequest | Where-Object Name -eq 'sAMAccountName' | ForEach-Object { $_.value }
    $logFile = "E:\SailPoint\Scripts\AD_After_Create\Logs\After_Create_Rule_$logDate_$Username.log"

    LogToFile("Starting SailPoint After Create Rule")

    #Escape double quotes to pass as a single string
    $requestAsString = $requestAsString -replace '"','\"'

    $Args = @"
-File $scriptPath -requestString "$requestAsString"
"@

    if($enableDebug){
        LogToFile("Start-Process arguments: $Args")
    }

    #SailPoint times out after 60 seconds if the After Create Rule does not finish execution, therefore Invoke-Expression has been replaced by Start-Process
    #Invoke the After Create script using start-process cmdlet. After invocation, this rule does not wait for the script invoked to finish execution
    logToFile("Invoking $scriptPath")
    Start-Process powershell.exe -ArgumentList $Args

    logToFile("$scriptPath invoked successfully")

}Catch{
    if (-not $logToFile){
        $logFile = "E:\SailPoint\Scripts\AD_After_Create\Logs\ERROR_After_Create_Rule_$logDate.log"
    }
    $ErrorMessage = $_.Exception.Message 
    $ErrorItem = $_.Exception.ItemName
    LogToFile("Error: Item = $ErrorItem -> Message = $ErrorMessage")
}


LogToFile("SailPoint completed execution")
