import java.util.HashMap;
import java.util.Map;

Map updatedMapInfo = new HashMap();
Map transientMap = (Map) application.getAttributeValue("transientValues");
if( transientMap != null ){
    log.error("Kiteworks: TransientMap");
    String newRefreshToken = (String) transientMap.get("custom_refreshtoken");
    Map connectorStateMap = new HashMap();
    connectorStateMap.put("custom_refreshtoken",newRefreshToken);
    updatedInfoMap.put("connectorStateMap",connectorStateMap);
    log.error("Kiteworks: Setting refresh token " + newRefreshToken);
}
log.error("Kiteworks: After rule.");
return updatedInfoMap;
