import sailpoint.connector.DelimitedFileConnector;

Map map = DelimitedFileConnector.defaultBuildMap( cols, record );
String accountStatus = (String) map.get( "status" );
if ( accountStatus != null ) {
if(accountStatus!=null && (accountStatus.equalsIgnoreCase("active") || accountStatus.equalsIgnoreCase("prehire"))){
map.put("IIQDisabled", false);
}
  if(accountStatus!=null && (accountStatus.equalsIgnoreCase("terminated") || accountStatus.equalsIgnoreCase("suspended") || accountStatus.equalsIgnoreCase("deleted"))){
map.put("IIQDisabled", true);
}
}
return map;
